            }
            function selection($value) {
                if ($_GET['modeTransport'] == $value) {
                    echo 'selection';
                }
            }
            function vide($value) {
                require_once 'admin/database.php';
                $db_vide = Database::connect();
                $statement_vide = $db_vide->query('SELECT * FROM billets WHERE ld_recherche = "' . $_GET['villeDepart'] . '" AND la_recherche = "' . $_GET['villeArrivee'] . '"AND mode_transport = "' . $value.'"');
                $result = $statement_vide->fetchAll(PDO::FETCH_ASSOC);
                
                if (count($result) == 0) {
                    echo ' empty';
                }
            }
